import React from "react";
import "./App.css";
import Form from "./components/Form"
import Data from "./components/Data"

class App extends React.Component{

    state={
        city: undefined,
        country: undefined,
        Temperature: undefined,
        Humidity : undefined,
        error: undefined
    };

    getWeather= async (e) => {
        e.preventDefault();
        const city="hyderabad";
        const country="india";
        const apiCall= await fetch(`https://openweathermap.org/data/2.5/weather?q=${city},${country}&appid=b6907d289e10d714a6e88b30761fae22`);
        const data=await apiCall.json();
        console.log(data);
        this.setState({
        city: data.name,
        country: data.sys.country,
        Temperature: data.main.temp,
        Humidity : data.main.humidity,
        error: undefined
        });
    }

    render(){
        return (
            <div>
                <Form onclick={this.getWeather}/>
                <Data city="this.state.city" country="this.state.country" 
                Temperature="this.state.Temperature" Humidity="this.state.Humidity" 
                error="this.state.error"/>
            </div>

        );
    }
}

export default App;
